import React, { useState, useRef, useEffect } from 'react';
import './start_page.scss';
import { gsap, TweenLite, Power1, TimelineLite } from "gsap";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";
// import { Controller, Scene } from 'react-scrollmagic';
import ScrollMagic, { Controller } from "scrollmagic";

const StartPage = props => {
	const boxRef = useRef(null);
	const tl = new TimelineLite();
	const controller = new ScrollMagic.Controller();


	const flightPath = {
		curviness: 1.25,
		autoRotate: true,
		path: [
			{ x: 100, y: -20 },
			{ x: 300, y: 10 },
			{ x: 500, y: 100 },
			{ x: 750, y: -100 },
			{ x: 350, y: -50 },
			{ x: 600, y: 100 },
			{ x: 800, y: 0 },
			{ x: window.innerWidth, y: -250 }
		]
	};
	useEffect(() => {
		gsap.registerPlugin(MotionPathPlugin);
		gsap.core.globals("MotionPathPlugin", MotionPathPlugin);
		ScrollMagic.Scene({
			triggerElement: ".animation",
			duration: 1000, // scroll distance
			triggerHook: 0
		})
			.setTween(".paperplane", TweenLite.to(boxRef.current, 1, { motionPath: flightPath, ease: Power1.easeInOut }
 ))
		.addTo(controller); // assign the scene to the controller


})
return (
	<div className="">
		<section className="container text">
			NOT YOUR AVERAGE ENGINEER
		</section >
		<section className="animation">
			<img ref={boxRef} className="paperplane" src="./paper.png" />
		</section>
	</div>
);
};

export default StartPage;
