import React, { useState, useRef, useEffect } from 'react';
import './start_page.scss';
import { gsap, TweenLite, Power1, TimelineLite } from "gsap";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";
import { ScrollTrigger } from "gsap/ScrollTrigger";


const StartPage = props => {
	const boxRef = useRef();
	const containerRef = useRef();
	const tl = new TimelineLite();
	const flightPath = {
		path: [
			{ x: 0, y: 0 },
			{ x: 0, y: 100 },
			{ x: 0, y: 200 },
			{ x: 0, y: 300 },
			{ x: 0, y: 400 },
			{ x: 0, y: 500 },
			{ x: 0, y: 600 },
			{ x: 0, y: 700 },
			{ x: 0, y: 750 },
		]
	};
	useEffect(() => {
		gsap.registerPlugin(MotionPathPlugin, ScrollTrigger);
		gsap.core.globals("MotionPathPlugin", MotionPathPlugin);
		gsap.core.globals("ScrollTrigger", ScrollTrigger);
		gsap.timeline({
			defaults: {},
			scrollTrigger: {
				trigger: containerRef.current,
				start: "top center",
				end: "bottom center",
				markers: true,
				scrub:true
			}
		}).to(boxRef.current, 1, { motionPath: flightPath, ease: easeInOut })
	})
	return (
		<div className="">
			<section className="container text">
				NOT YOUR AVERAGE ENGINEER
			</section >
			<section ref={containerRef} className="animation">
				<img ref={boxRef} className="paperplane" src="./paper.png" />
			</section>
					<section className="container text">
				NOT YOUR AVERAGE ENGINEER
			</section >

		</div>
	);
};

export default StartPage;
