import React, { useState, useRef, useEffect } from 'react';
import './start_page.scss';
import { gsap,TweenLite,Power1 } from "gsap";
const StartPage = props => {
	const boxRef = useRef(null);
	const flightPath = {
	curviness:1.25,
	autoRotate:true,
	values:[{x:100,y:-20},{x:300,y:10}]
	};
	useEffect(() => {
		TweenLite.to(boxRef.current,1, {target:0,y:50,x:100,ease:Power1.easeInOut} )
		TweenLite.to(boxRef.current,1, {target:1,y:100,x:0,ease:Power1.easeInOut} )
		TweenLite.to(boxRef.current,1, {target:2,y:-50,x:300,ease:Power1.easeInOut} )
	})
	return (
		<div className="">
			<section className="container text">
				NOT YOUR AVERAGE ENGINEER
			</section >
			<section className="animation">
				<img ref={boxRef} className="paperplane" src="./paper.png" />
			</section>
		</div>
	);
};

export default StartPage;
