import React, { useState, useRef, useEffect } from 'react';
import './start_page.scss';
import { gsap, TweenLite, TimelineLite, Power1, TweenMax,Linear } from "gsap";
const StartPage = props => {
	const tl = new TimelineLite()
	const boxRef = useRef(null);
	const flightPath = {
		curviness: 1.25,
		autoRotate: true,
		values: [{ x: 100, y: -20 }, { x: 300, y: 10 }]
	};
	useEffect(() => {
		TweenMax.to(boxRef.current, 1, {
			bezier: {
				type: "soft",
				values: [{ x: 10, y: 30 }, { x: -30, y: 20 }, { x: -40, y: 10 }, { x: 30, y: 20 }, { x: 10, y: 30 }],
				autoRotate: true
			},
			ease: Linear.easeNone,
			repeat: -1
		}, "start+=3");
	})
	return (
		<div className="">
			<section className="container text">
				NOT YOUR AVERAGE ENGINEER
			</section >
			<section className="animation">
				<img ref={boxRef} className="paperplane" src="./paper.png" />
			</section>
		</div>
	);
};

export default StartPage;
