import React, { useState, useRef, useEffect } from 'react';
import './start_page.scss';
import { gsap,TweenLite,TimelineLite,Power1 } from "gsap";
const StartPage = props => {
	const tween = new TimelineLite()
	const boxRef = useRef(null);

	const flightPath = {
	curviness:1.25,
	autoRotate:true,
	values:[{x:100,y:-20},{x:300,y:10}]
	};

	useEffect(() => {
		tween.add(TweenLite.to(boxRef.current,1,{bezier:flightPath,ease:Power1.easeInOut}))
	})
	return (
		<div className="">
			<section className="container text">
				NOT YOUR AVERAGE ENGINEER
			</section >
			<section className="animation">
				<img ref={boxRef} className="paperplane" src="./paper.png" />
			</section>
		</div>
	);
};

export default StartPage;
