import React, { useState, useRef, useEffect } from 'react';
import './start_page.scss';
import { gsap, TweenLite, Power1,TimelineLite } from "gsap";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";


const StartPage = props => {
	const boxRef = useRef(null);
	const tl = new TimelineLite();
	const flightPath = {
		curviness: 1.25,
		autoRotate: true,
		values: [{ x: 100, y: -20 }, { x: 300, y: 10 }]
	};
	useEffect(() => {
		gsap.registerPlugin(MotionPathPlugin);
		gsap.core.globals("MotionPathPlugin", MotionPathPlugin);
		gsap.to(boxRef.current, 1, { motionPath: { path: [{ x: 100, y: 100 }] }, ease: Power1.easeInOut })
	})
	return (
		<div className="">
			<section className="container text">
				NOT YOUR AVERAGE ENGINEER
			</section >
			<section className="animation">
				<img ref={boxRef} className="paperplane" src="./paper.png" />
			</section>
		</div>
	);
};

export default StartPage;
